export const phoneValidator = control => {
  if (control.value?.length >= 10 && !preffixShouldStartsWithZero(control.value)) {
    return {
      invalidPreffix: control.value
    };
  }

  if (control.value?.length >= 10 && !!phoneStartsWithNine(control.value)) {
    return {
      invalidPhone: control.value
    };
  }

  return null;
};

const phoneStartsWithNine = value => {
  const phone = value.substring(3);

  if (phone.charAt(0) === '9') {
    return true;
  }

  return false;
};

const preffixShouldStartsWithZero = value => {
  const preffix = value.substring(0, 2);

  if (preffix.charAt(0) === '0') {
    return true;
  }

  return false;
};
